'use strict';

var express = require('express'),
    controller = require('./csv.controller'),
    router = express.Router(),
    MulterImpl = require('../../middleware/MulterImpl.js'),
    csvImporter = new MulterImpl({uplodaDir: 'uploads/'}).init();

router.get('/', controller.search);
router.get('/download/file/:filename', controller.fileDownload);
router.get('/download/:id/:tenantId', controller.download);
router.get('/synonyms', controller.synonyms);
router.post('/preview', controller.preview);
router.get('/:id', controller.getMapping);
router.post('/ignore', controller.ignorecreate);
router.post('/upload',csvImporter.any(), controller.uploadCSV);
router.post('/', controller.create);
router.put('/:id/:tenantId', controller.update);
router.delete('/:id', controller.delete);
router.get('/import/:id/:tenantId', controller.import);
router.post('/import', controller.directImport);
router.post('/parse', controller.parse);
router.post('/masterdata', controller.masterdata);
router.post('/master',csvImporter.any(), controller.uploadMaster);

module.exports = router;

