'use strict';

var utilParser = require('../../utils/parser'),
    models = require('../../models'),
    synonym = require('../../utils/synonyms.js'),
    schema = require('../../utils/schema.js'),
    tablelist = require('../../utils/tablelist.js'),
    masterservice = require('../../utils/masterservice.js'),
    moment = require('moment');

// parser object
var parser = new utilParser(schema);

/**
 * function takes care of the response callback
 */
function sendresponse(res) {
  return function (error, data) {
    if (error) res.status(500).json(error);

    res.status(200).json(data);
  };
}

/*
 * End point returns the synonymsList
 */
exports.synonyms = function (req, res) {
  res.json({ synonym: synonym.synonymsList(), tablelist: tablelist, masterschema: masterservice.masterSchema() });
};

/**
 * End point which response preview data and accept file
 * to be uploaded
 */
exports.uploadCSV = function (req, res) {
  if (req.body) {
    parser.upload(req.files[0].filename, req.files[0].originalname, function (uploaderror, newfilename) {
      if (uploaderror) {
        res.status(500).json(uploaderror);
      } else {
        parser.preview(newfilename, null, sendresponse(res));
      }
    });
  }
};

/**
 * End point which response preview data
 */
exports.preview = function (req, res) {
  if (req.body) parser.preview(req.body.fileName, req.body, sendresponse(res));else res.status(500).json('No data available');
};

/**
 * End point used to create mapping data and response the parsed data
 */
exports.create = function (req, res) {
  res.setTimeout(0);
  var mappingname = req.body.mapping.mappingName;

  models.mapping.create({
    attributeId: req.body.upload.fileinfo.name ? req.body.upload.fileinfo.name : req.body.mapping.attributeId,
    tenantId: '2',
    fileName: req.body.upload.fileinfo.name ? req.body.upload.fileinfo.name : req.body.mapping.attributeId,
    mappingInfo: req.body.mapping.mappingData,
    delimiter: req.body.preview.delimiter,
    encoding: req.body.preview.encoding,
    quotes: req.body.preview.quotes,
    dateFormat: req.body.preview.dateFormat,
    headerrow: req.body.preview.headerrow,
    numberFormat: req.body.preview.numberFormat,
    mappingName: mappingname,
    filetype: req.body.preview.type
  }).then(function (result) {
    parser.sample(result.fileName, result, sendresponse(res));
  })['catch'](function (error) {
    res.status(500).json(error);
  });
};

/**
 * End point used to update mapping data and response the parsed data
 */
exports.update = function (req, res) {
  res.setTimeout(0);

  var data = req.body;
  var updateparams = {
    tenantId: req.params.tenantId,
    mappingInfo: data.mapping.mappingData,
    mappingName: data.mapping.mappingName,
    delimiter: data.preview.delimiter,
    dateFormat: data.preview.dateFormat,
    headerrow: data.preview.headerrow,
    numberFormat: data.preview.numberFormat,
    filetype: data.preview.type
  };

  if (data.upload && data.upload.fileinfo) {
    updateparams.fileName = data.upload.fileinfo.name;
    updateparams.attributeId = data.upload.fileinfo.name;
  }

  models.mapping.find({
    where: { tenantId: req.params.tenantId, mappingName: req.params.id }
  }).then(function (mapping) {
    mapping.updateAttributes(updateparams).then(function (mapping) {
      parser.sample(mapping.fileName, mapping, sendresponse(res));
    })['catch'](function (error) {
      res.status(500).json(error);
    });
  })['catch'](function (error) {
    res.status(500).json(error);
  });
};

/**
 * End point used to parse and download the mapping information
 */
exports.download = function (req, res) {
  res.setTimeout(0);
  models.mapping.findOne({
    where: { tenantId: req.params.tenantId, mappingName: req.params.id }
  }).then(function (mapping) {
    parser.parse(mapping.fileName, mapping, function (error, filename) {
      res.download(filename);
    });
  })['catch'](function (error) {
