/**
 * excel parser
 *
 * NOTE: A class which does all the excel related parsing functionalities
 */

var es = require('event-stream'),
csv_mapping = require('./mapping'),
changeFormat = require('./typeparser.js'),
fs = require('fs'),
iconv = require('iconv-lite'),
_ = require('underscore'),
xlsx = require('xlsx'),
excelStream = require('./excel-stream');

var columnstring = '';
var getJSONDB = null;
var consolidateObj = null;
var mandatoryfields = null;
var JSON_database_table = '';
var mastertable = '';
var masterprimarykey = '';

/**
 * check is the given object is function or not
 * @param functionToCheck - function
 *
 * @return boolean
 */
function isFunction(functionToCheck) {
  return typeof functionToCheck == 'function';
}

/**
 * EXCEL parser class
 * @param jsondb - function
 * @param consolidate - function
 */
var excelParser = function(jsondbFunc, consolidateFunc, mandatoryfieldsObject, json_table_name, columnname, masterinfo) {
  if (jsondbFunc && consolidateFunc && isFunction(jsondbFunc) && isFunction(consolidateFunc)) {
    getJSONDB = jsondbFunc;
    consolidateObj = consolidateFunc;
    mandatoryfields = mandatoryfieldsObject;
  } else {
    throw new Error('Expected getJSONDB and consolidateObj functions');
    process.exit();
  }
  if (json_table_name && columnname) {
    JSON_database_table = json_table_name;
    columnstring = columnname;
  } else {
    throw new Error('Expected json_table_name and columnname');
    process.exit();
  }
  if(masterinfo && masterinfo.table && masterinfo.primarykey) {
    mastertable = masterinfo.table;
    masterprimarykey = masterinfo.primarykey;
  } else {
    throw new Error('Expected master table and master primary key');
    process.exit();
  }
}

/**
 * function to preparse the mapping information
 * @param mappinginfo - Object, mapping information
 * @return object - {mapped_data, tablename}
 */
var preParseInfo = function(mappinginfo) {
  var mapped_data = _.findWhere(mappinginfo, {field: masterprimarykey, table: mastertable});
  var groupe = _.groupBy(mappinginfo, 'table');
  var tablename = {};
  var removedtablename = {};

  for (var table in groupe) {
    for (var i = 0; i < groupe[table].length; i++) {
      var add = true;
      if (tablename[groupe[table][i].sheet]) {
        if (tablename[groupe[table][i].sheet] != table) {
          add = false;
        }
      }

      if (removedtablename[groupe[table][i].sheet])
        add = false;

      if (!add) {
        delete(tablename[groupe[table][i].sheet]);
        removedtablename[groupe[table][i].sheet] = {};
      }
      else {
        tablename[groupe[table][i].sheet] = table;
      }
    }
  }

  return {mapped_data: mapped_data, tablename: tablename};
}

/**
 * function to pre consolidate based on the master key value
 * @param db - database object
 * @param jsondata - Object
 * @param tablename - String, name of the table
 * @param masterkeyvalue - value for the master key
 */
var preConsolidateObj = function(db, jsondata, tablename, masterkeyvalue) {
  var findobj = {};
  var updated = false;

  findobj[mastertable] = {};
  findobj[mastertable][masterprimarykey] = masterkeyvalue;

  var table_to_json = jsondata[mastertable][tablename];

  var updatequery = db.get(JSON_database_table);
  var findchild = mastertable + "." + tablename;

  if (updatequery.find(findobj).value() && table_to_json) {
    if (updatequery.find(findobj).get(findchild).value()) {
      for (var d = 0; d < table_to_json.length; d++) {
          updatequery.find(findobj).get(findchild).push(table_to_json[d]).value();
      }
      updated = true;
    } else {
      updatequery.find(findobj).set(findchild, table_to_json).value();
      updated = true;
    }
  }

  return updated;
}

/**
 * function to tranform the object as per the json data and mapping information
 * @param jsondata - Object, json from sheet
 * @param mapping - Object, mapping information
 * @return JSON
 */
var transform = function(jsondata, mapping) {
  var json = {};
  var newjson = csv_mapping(jsondata, mapping.mappingInfo, mapping.dateFormat, mapping.numberFormat, mandatoryfields);
  Object.keys(json).forEach(function(key) { delete json[key]; });
  Object.keys(newjson).forEach(function(key) {
    if (!json.action) {
      json.action = 'insert-update';
      json.product = {};
    }
    if (key !== 'tenantId')
      json.product[key] = newjson[key];
  });
  return json;
}

/**
 * An intelligent function detect all the required parameters from the CSV file,
 * which helps in parsing the file
 *
 * @param filepath - full path including the filename
 * @param samplemaxrow - number rows to be parsed for the sample
 * @param ignoredencodings - ignored encodings
 * @param quotesdetector - function
 * @param delimiterdetector - function
 * @param charsetdetector - function
 * @param callback - function
 */
excelParser.prototype.detector = function(filepath, samplemaxrow, ignoredencodings, quotesdetector, delimiterdetector, charsetdetector, callback) {
  var detectedencoding = '';
  var resultpreviewdata = {}, lines = [];

  var workbook = xlsx.readFile(filepath);

  var sheetscount = workbook.SheetNames.length;
  var parsedsheetcount = 0;

  /**
   * callback to be parsed at the end of the parsing statement
   */
  var linedetector = function() {
    var delimiter = delimiterdetector(lines[0]);
    var quotes = quotesdetector(lines[0], delimiter);
    var splitter = quotes + delimiter + quotes;
    var resultpreviewdata = {};

    resultpreviewdata.delimiter = delimiter;
    resultpreviewdata.quotes = quotes;
    resultpreviewdata.encoding = detectedencoding ? detectedencoding.encoding : null;
    resultpreviewdata.headerrow = 1;

    callback(null, resultpreviewdata);
  }

  /**
   * closure function to read the excel file for the
   * @param sheetname - name of the sheet
   * @param cb - callback
   */
  function readFile(sheetname) {
    var parsedlines = 0;
    var stream = fs.createReadStream(filepath)
    .pipe(excelStream({
       sheet: sheetname
    })).pipe(es.mapSync(function(line) {
      detectedencoding = charsetdetector(line);
      detectedencoding = ignoredencodings.indexOf(detectedencoding) > -1 ? defaultencoding : detectedencoding;

      // lines.push(line);
      lines.push("") // temporary

      if (lines.length >= samplemaxrow) {
        stream.end();
        linedetector();
      }
    }));
  }

  if (sheetscount > 0) {
    readFile(workbook.SheetNames[0]);
  }

}

/**
 * function parse for the preview data
 * @param completefilepath - full path including the filename
 * @param headerColumn - array of headers
 * @param samplemaxrow - sample rows
 * @param preview - preview data
 * @param callback - function
 */
excelParser.prototype.preview = function(completefilepath, headerColumn, samplemaxrow, preview, callback) {
  var fileStream = fs.createReadStream(completefilepath);
  var resultpreviewdata = preview;
  var linenumber = parseInt(preview.headerrow) > -1 ? parseInt(preview.headerrow) : 0;
  var maxrow = linenumber + samplemaxrow;

  var resultdata = {};

  var workbook = xlsx.readFile(completefilepath);

  var sheetscount = workbook.SheetNames.length;
  var parsedsheetcount = 0;

  /**
   * closure function to read the excel file for the
   * @param sheetname - name of the sheet
   * @param cb - callback
   */
  function readfile(sheetname, cb) {
    var parsedlines = 0;
    if (linenumber === 0)
     resultdata[sheetname] = [headerColumn];
    var stream = fs.createReadStream(completefilepath)
    .pipe(excelStream({
       sheet: sheetname,
       columns: headerColumn
    })).pipe(es.mapSync(function(line) {
      if (parsedlines < maxrow) {
       var data = _.toArray(line);
       if (!resultdata[sheetname] && parsedlines == linenumber - 1) {
         resultdata[sheetname] = [data];
       } else if (parsedlines >= linenumber && parsedlines < maxrow) {
         var thing = {};
         var length = resultdata[sheetname][0].length;
         for(var y = 0; y < length; y++) {
           var key = resultdata[sheetname][0][y];
           thing[key] = data[y] ? changeFormat(isNaN(data[y]) ? 'string' : 'number', data[y], preview.dateFormat, preview.numberFormat) : '';
         }
         resultdata[sheetname].push(thing);
      }
      parsedlines++;

      } else if (parsedlines > maxrow) {
        stream.end();
      }
    })).on('end', function() {
      cb(null, resultdata);
    });
  }

  for (var sheet = 0; sheet < sheetscount; sheet++) {
    readfile(workbook.SheetNames[sheet], function(error, resultdata) {
      if (error) {
        callback(error);
      } else if (++parsedsheetcount == sheetscount) {
        resultpreviewdata.resultdata = resultdata;
        callback(null, resultpreviewdata);
      }
    });
  }
}

/**
