  var workbook = xlsx.readFile(filepath);
  var sheetNames = workbook.SheetNames;
  var sheetscount = sheetNames.length;
  var parsedsheetcount = 0;
  var resultdata = {};
  var donelist = {};

  // read excel file
  function readExcel(sheetname, cb) {
    var headers = [];
    // end of reading the excel sheet
    function endRead() {
      cb(true, sheetname);
      if (sheetname == mapped_data.sheet) {
        for (var sheet = 0; sheet < sheetscount; sheet++) {
            readExcel(sheetNames[sheet], parseExceldata);
        }
      }
    }

