var upperCase = function(str) {
	if(str)
      return str.toString().toUpperCase();
	return str;
};

var lowerCase = function(str) {
	if(str)
      return str.toString().toLowerCase();
	return str;
};

var removeWhitespace = function(str) {
	if(str)
      return str.toString().split(' ').join('');
	return str;
};

var substring = function(str, param1, param2) {
	if(str) {
		if (parseInt(param2) >= parseInt(param1)) {
			return str.toString().substr(param1, param2);
		} else {
			return str.toString().substr(param1);
		}
	}
	return str;
}

var replace = function(str, param1, param2) {
	if(str) {
		str = str.toString().replace(param1, param2);
	}
	return str;
}

var replaceall = function(str, param1, param2) {
	if (str) {
		var stringarr = str.toString().split(param1);
		str = stringarr.join(param2);
	}
	return str;
}

var math = function(str, mathfunc, param) {
	var value = parseInt(str);
	var constraint = parseInt(param);
	if (!isNaN(value)) {
		switch(mathfunc) {
			case 'add':
				return value + constraint;
				break;
			case 'multiply':
				return value * constraint;
				break;
			case 'divide':
				return value / constraint;
				break;
			case 'sub':
				return value - constraint;
				break;
		}
	}
	return str;
}

var truncate = function(str, after, param) {
	if (str) {
		var parampos = str.toString().indexOf(param);
		parampos = parampos < 0 ? -1 : parampos;
		if (parampos > -1) {
			var startpos = 0;
			var endpos = parampos;
			if (after) {
				startpos = parampos + param.length;
				endpos = str.toString().length;
			}
			str = str.toString().substr(startpos, endpos);
		}
		return str;
	}
	return str;
}

var attach = function(str, after, param) {
	if (after) {
		return str + param;
	}
	return param + str;
}

var firstorlast = function(str, last, param) {
	if (str) {
		var startpos = 0;
		if (last) {
			var strlength = str.length;
			startpos = strlength - parseInt(param);
			return str.toString().substr(startpos, strlength);
		}
		return str.toString().substr(startpos, param);
	}
	return str;
}

