  switch(type.toLowerCase()) {
    case 'array':
      var arraydata = [].concat(value);
      for (var i = 0; i < arraydata.length; i++) {
          arraydata[i] = changeFormat('', value, dateFormat, numberFormat);
      }
      return arraydata;
    break;
    case 'number':
      var newvalue = parseFloat(value);
      return !isNaN(newvalue) ? formatNumber(newvalue) : 0;
    break;
    case 'boolean':
      var newvalue = value && value.toLowerCase() == "true" ? true : false;
      return newvalue;
    default:
      return changeDataFormat(value, dateFormat, numberFormat);
    break;
  }
}

//data format change
var formatDate = function(data, todateformat) {
  if(data) {
    if (moment(data, "DD-MM-YYYY", true).isValid()) {
      data = moment(data, "DD-MM-YYYY").format(todateformat);
    } else if (moment(data, "MM/DD/YYYY", true).isValid()) {
      data = moment(data, "MM/DD/YYYY").format(todateformat);
    } else if (moment(data, "YYYY-MM-dd", true).isValid()) {
