        }
        if (str[1] && str[1].length >= 4) {
            str[1] = str[1].replace(/(\d{3})/g, '$1 ');
        }
        data = str.join('.');
        if(data.toString().indexOf('.') < 0) {
            data = data + '.00';
        }
      }
