/**
 * csv parser
 *
 * NOTE: A class which does all the csv related parsing functionalities
 */

var es = require('event-stream'),
csv_mapping = require('./mapping'),
changeFormat = require('./typeparser.js'),
fs = require('fs'),
iconv = require('iconv-lite'),
_ = require('underscore'),
Converter = require("csvtojson").Converter;

var columnstring = '';
var getJSONDB = null;
var consolidateObj = null;
var mandatoryfields = null;
var JSON_database_table = '';
var mastertable = '';
var masterprimarykey = '';

/**
 * check is the given object is function or not
 * @param functionToCheck - function
 *
 * @return boolean
 */
function isFunction(functionToCheck) {
  return typeof functionToCheck == 'function';
}

/**
 * CSV parser class
 * @param jsondbFunc - function
 * @param consolidateFunc - function
 */
var csvParser = function(jsondbFunc, consolidateFunc, mandatoryfieldsObject, json_table_name, columnname, masterinfo) {
  if (jsondbFunc && consolidateFunc && isFunction(jsondbFunc) && isFunction(consolidateFunc)) {
    getJSONDB = jsondbFunc;
    consolidateObj = consolidateFunc;
    mandatoryfields = mandatoryfieldsObject;
  } else {
    throw new Error('Expected getJSONDB and consolidateObj functions');
    process.exit();
  }
  if (json_table_name && columnname) {
    JSON_database_table = json_table_name;
    columnstring = columnname;
  } else {
    throw new Error('Expected json_table_name and columnname');
    process.exit();
  }
  if(masterinfo && masterinfo.table && masterinfo.primarykey) {
    mastertable = masterinfo.table;
    masterprimarykey = masterinfo.primarykey;
  } else {
    throw new Error('Expected master table and master primary key');
    process.exit();
  }
}

/**
 * transform, perform transformation on the parsing data
 * @param headers - headers array
 * @param mapping - mapping object
 * @param json - json object
 * @param jsonarray - array of json data
 * @param row - row object
 * @param index - current index
 * @return object
 */
var transform = function(headers, mapping, json, jsonarray, row, index) {
  var newjson = {}, jsondata = {};

  for (var h = 0; h < headers.length; h++) {
    var columnname = columnstring + (h + 1);
    jsondata[headers[h]] = json[columnname];
  }

  newjson = csv_mapping(jsondata, mapping.mappingInfo, mapping.dateFormat, mapping.numberFormat, mandatoryfields);
  Object.keys(json).forEach(function(key) { delete json[key]; });
  Object.keys(newjson).forEach(function(key) {
    if (!json.action) {
      json.action = 'insert-update';
      json.product = {};
    }
    if (key !== 'tenantId')
      json.product[key] = newjson[key];
  });
  return json;
}


/**
 * An intelligent function detect all the required parameters from the CSV file,
 * which helps in parsing the file
 *
 * @param filepath - full path including the filename
 * @param samplemaxrow - number rows to be parsed for the sample
 * @param ignoredencodings - ignored encodings
 * @param quotesdetector - function
 * @param delimiterdetector - function
 * @param charsetdetector - function
 * @param callback - function
 */
csvParser.prototype.detector = function(filepath, samplemaxrow, ignoredencodings, quotesdetector, delimiterdetector, charsetdetector, callback) {
  var detectedencoding = '';
  var resultpreviewdata = {}, lines = [];

  var stream = fs.createReadStream(filepath);

  /**
   * callback to be parsed at the end of the parsing statement
   */
  var linedetector = function() {
    var delimiter = delimiterdetector(lines[0]);
    var quotes = quotesdetector(lines[0], delimiter);
    var splitter = quotes + delimiter + quotes;
    var resultpreviewdata = {};

    resultpreviewdata.delimiter = delimiter;
    resultpreviewdata.quotes = quotes;
    resultpreviewdata.encoding = detectedencoding ? detectedencoding.encoding : null;
    resultpreviewdata.headerrow = 1;

    callback(null, resultpreviewdata);
  }

  /**
   * a callback function to be called on the end of line parsing
   */
  var parseLine = function() {
    return es.mapSync(function(line) {
      detectedencoding = charsetdetector(line);
      detectedencoding = ignoredencodings.indexOf(detectedencoding) > -1 ? defaultencoding : detectedencoding;

      var csv = line.toString().split('\r\n');
      var csvlength = csv.length;

      for (var index = 0; index < csvlength; index++) {
        if (lines.length >= samplemaxrow) {
          break;
        } else if(csv[index].length > 0) {
          lines.push(csv[index]);
        }
      }

      if (lines.length >= samplemaxrow) {
        stream.close();
        linedetector();
      }
    });
  }

  stream.pipe(parseLine()).on('error', callback).on('end', linedetector);
}

/**
 * function parse for the preview data
 * @param completefilepath - full path including the filename
 * @param headerColumn - array of headers
 * @param samplemaxrow - sample rows
 * @param preview - preview data
 * @param callback - function
 */
csvParser.prototype.preview = function(completefilepath, headerColumn, samplemaxrow, preview, callback) {
  var fileStream = fs.createReadStream(completefilepath);
  var splitter = preview.quotes + preview.delimiter + preview.quotes;
  var resultpreviewdata = preview;
  var line = parseInt(preview.headerrow) > -1 ? parseInt(preview.headerrow) : 0;
  var maxrow = line + samplemaxrow, parsedcount = 0;

  var resultdata = [];

  var headerConfig = {
    constructResult: true,
    noheader: true,
    delimiter: preview.delimiter,
    maxRowLength: maxrow
  }

  if (preview.quotes.length > 0) {
    headerConfig.quote = preview.quotes;
  }

  var csvConverter = new Converter(headerConfig);

  // conversion
  var responsesend = false;
  var parsecount = 0;
  var headeradded = false;

  if (line - 1 < 0) {
    resultdata[0] = headerColumn;
    headeradded = true;
  }
  csvConverter.on('record_parsed', function(jsonObj, rawRow, rowIndex) {
     var data = _.toArray(jsonObj);
     if (!headeradded && parsecount == line - 1) {
       resultdata[0] = data;
     } else if (parsecount >= line && parsecount < maxrow) {
       var newarray = [];
       var thing = {};
       for(var y = 0; y < resultdata[0].length; y++) {
         var key = resultdata[0][y];
         thing[key] = data[y] ? changeFormat(isNaN(data[y]) ? 'string' : 'number', data[y], preview.dateFormat, preview.numberFormat) : '';
       }
       resultdata.push(thing);
    }
    parsecount++;
  }).on('end_parsed', function() {
    if (parsecount < 1 && !responsesend) {
      responsesend = true;
      callback("Incorrect file content");
    }
    if (!responsesend) {
      responsesend = true;
      resultpreviewdata.resultdata = resultdata;
      callback(null, resultpreviewdata);
    }
  });

  fileStream.pipe(iconv.decodeStream(preview.encoding)).pipe(csvConverter);
  fileStream.on('end', function() {
    if (!responsesend) {
      if (parsecount < 1) {
        responsesend = true;
        callback("Incorrect file content", null);
      } else {
        responsesend = true;
        resultpreviewdata.resultdata = resultdata;
        callback(null, resultpreviewdata);
      }
    }
  });
}

/**
 * function to parse the sample data
 * @param filepath - full file path
 * @param maxrow - number of rows to be parsed
 * @param headerColumn - array of headers
 * @param mapping - mapping information along with other informations
 * @param callback - function
 */
csvParser.prototype.sample = function(filepath, maxrow, headerColumn, mapping, callback) {
  var fileStream = fs.createReadStream(filepath);
  var convertedJSON = [], headers = [], responsesend = false;
  var rowindex = mapping.headerrow - 1;
  var maxRowLength = parseInt(mapping.headerrow) + maxrow;
  var quotes = mapping.quotes.toString();

  var db = getJSONDB(mapping.mappingName);

  var headerConfig = {
    constructResult: true,
    delimiter: mapping.delimiter,
    maxRowLength: maxRowLength
  }
  if (quotes.length > 0) {
    headerConfig.quote = quotes;
  }

  headerConfig.noheader = true;
  headerConfig.headers = headerColumn;
  headers = headerColumn;
  var csvConverter = new Converter(headerConfig);

  csvConverter.transform = function(json, row, index) {
    var jsondata = _.toArray(json);
    if (index == rowindex) {
      headers = jsondata;
      Object.keys(json).forEach(function(key) { delete json[key]; });
    } else if (index > rowindex && index <= maxRowLength) {
      json = transform(headers, {mappingInfo: mapping.mappingInfo,
       numberFormat: mapping.numberFormat}, json, jsondata, row, index);
       consolidateObj(db, json);
    } else {
     Object.keys(json).forEach(function(key) { delete json[key]; });
    }
  }

  fileStream.pipe(iconv.decodeStream(mapping.encoding)).pipe(csvConverter);
  fileStream.on('end', function() {
    if (!responsesend) {
      convertedJSON = db.get(JSON_database_table).value();
      callback(null, {id: mapping.id, name: mapping.mappingName, tenantid: mapping.tenantId, json: convertedJSON});
    }
  });

}

/**
 * function to parse the entire file
 * @param filepath - complete filepath
 * @param jsonfilepath - complete dest filepath
 * @param headerColumn - array of header columns
 * @param mapping - mapping information
 * @param callback - a function callback
 */
csvParser.prototype.parse = function(filepath, jsonfilepath, headerColumn, mapping, callback) {
  var fileStream = fs.createReadStream(filepath);
  var writeStream = fs.createWriteStream(jsonfilepath);
  var headers = [];

  var mappingname = mapping.mappingName;

  var db = getJSONDB(mappingname);

  var headerConfig = {
    constructResult: false,
    toArrayString: true,
    delimiter: mapping.delimiter,
    ignoreEmpty: true
  }

  if (mapping.quotes.length > 0) {
    headerConfig.quote = mapping.quotes;
  }

  headerConfig.noheader = true;
  headerConfig.headers = headerColumn;
  headers = headerColumn;

  var csvConverter = new Converter(headerConfig);
  var rowindex = mapping.headerrow - 1;

  csvConverter.transform = function(json, row, index) {
    var jsondata = _.toArray(json);
    if (index == rowindex) {
      headers = jsondata;
      Object.keys(json).forEach(function(key) { delete json[key]; });
    } else if (index > rowindex) {
      json = transform(headers, {mappingInfo: mapping.mappingInfo,
       numberFormat: mapping.numberFormat}, json, jsondata, row, index);
       consolidateObj(db, json);
    } else {
     Object.keys(json).forEach(function(key) { delete json[key]; });
    }
  }

  fileStream.pipe(iconv.decodeStream(mapping.encoding)).pipe(csvConverter);
  fileStream.on('end', function() {
    writeStream.write(JSON.stringify(db.get(JSON_database_table).value()), function() {
      callback(null, jsonfilepath);
    });
  });
}

module.exports = csvParser;

