    _.filter(tabledata, function(val, key) {
      var exists = true;
      for (var param in params) {
        if (val[param] && val[param].toString && val[param].toString() && val[param].toString().toLowerCase().indexOf(params[param].value.toLowerCase()) > -1 && exists)
          exists = true;
        else
          exists = false;
      }
      totalcount += exists ? 1 : 0;

      var returnvalue = addcount < pagination.limit && totalcount > pagination.offset ? exists: false;
      addcount += returnvalue ? 1 : 0;

      if (returnvalue) {
        var resultobj = {};
        for (var property in reversmapprecords) {
            resultobj[property] = val[property];
            labels[property] = reversmapprecords[property].label;
        }
        filtereddata.push(resultobj);
      }

      return returnvalue;
    });

    return {count: totalcount, data: filtereddata, labels: labels};
  }

  return {count: 0, data: []};
}

