  var filesep = originalname.split('.');
  var newfilename = filename + '.' + filesep[filesep.length - 1];
  var filepath = uploaddirectory + filename;
  var newfilepath = uploaddirectory + newfilename;

  fs.rename(filepath, newfilepath, function(err) {
    callback(err, newfilepath);
  });
