  * @param originalname - original name of the file
  * @param callback - callback function to be called after parsing the data
  */
 exports.parseupload = function(filename, originalname, callback) {
   var json = {};
   var donelist = [];

   var parseJson = function(sheetname, linedata) {
     var fields = fieldstoread[sheetname], obj = {};
     for (var key in fields) {
       obj[fields[key].field] = linedata[key] ? linedata[key] : '';

       if (fields[key].map) {
         var map = fields[key].map;
         for (var table in map) {
           var length = json[table] ? json[table].length : 0;
           var currentvalue = obj[map[table].thisid];
           var targetkey = map[table].key;
           var targetfield = map[table].field;

           for (var i = 0; i < length; i++) {
             var targetvalue = json[table][i][targetkey];
             if (currentvalue == targetvalue) {
               json[table][i][targetfield] = targetvalue;
             }
           }
         }
       }
     }
     json[sheetname].push(obj);
   }

   var readExcel = function(filepath, sheetname, done) {
     var stream = fs.createReadStream(filepath)
    .pipe(excelStream({
       sheet: sheetname
    })).pipe(es.mapSync(function(line) {
      if (!json[sheetname])
        json[sheetname] = [];

      parseJson(sheetname, line);
    })).on('end', function() {
      done(sheetname);
    });
   }

   var writetofile = function() {
     fs.writeFile(staticdatapath + staticjsonfilename, JSON.stringify(json), function (err) {
      if (err) return callback(err);
      callback(null);
    });
   }

   var parsefileandstore = function(err, newfilepath) {
     if (err) {
       callback(err);
     } else {
      // read sheets
      var workbook = xlsx.readFile(newfilepath);
      var sheetNames = workbook.SheetNames;
      for (var i in sheettoread) {
        var sheet = sheettoread[i];
        if (sheetNames.indexOf(sheet) > -1) {
          readExcel(newfilepath, sheet,  function(sheetname) {
            donelist.push(sheetname);
            if (donelist.length > 2) {
              writetofile();
            }
          });
        } else {
          donelist.push(sheet);
        }
        if (donelist.length > 2) {
          writetofile();
        }
      }
      // readonly this sheets
     }
   }
   uploadrename(filename, originalname, parsefileandstore);
 }

