var moment = require('moment');

let changeFormat = function(type, value, dateFormat, numberFormat) {
  switch(type.toLowerCase()) {
    case 'array':
      var arraydata = [].concat(value);
      for (var i = 0; i < arraydata.length; i++) {
          arraydata[i] = changeFormat('', value, dateFormat, numberFormat);
      }
      return arraydata;
    break;
    case 'number':
      var newvalue = parseFloat(value);
      return !isNaN(newvalue) ? formatNumber(newvalue) : 0;
    break;
    case 'boolean':
      var newvalue = value && value.toLowerCase() == "true" ? true : false;
      return newvalue;
    default:
      return changeDataFormat(value, dateFormat, numberFormat);
    break;
  }
}

//data format change
var formatDate = function(data, todateformat) {
  if(data) {
    if (moment(data, "DD-MM-YYYY", true).isValid()) {
      data = moment(data, "DD-MM-YYYY").format(todateformat);
    } else if (moment(data, "MM/DD/YYYY", true).isValid()) {
      data = moment(data, "MM/DD/YYYY").format(todateformat);
    } else if (moment(data, "YYYY-MM-dd", true).isValid()) {
      data = moment(data, "YYYY-MM-dd").format(todateformat);
    }
  }
  return data;
}
var formatNumber = function(data, numberformat) {
  if (data) {
    switch(numberformat) {
    case '#,##':
      if(data.toString().indexOf(',')<0) {
        data = data + ',00';
      }
      break;
    case '#.##':
      if(numberformat.toString().indexOf('.')<0)
        data = data + '.00';
      break;
    case '#,###.##':
      if (data.toString().length > 5) {
        data = (data*100 / 100);
        var str = data.toString().split('.');
        if (str[0].length >= 4) {
            str[0] = str[0].replace(/(\d)(?=(\d{3})+$)/g, '$1,');
        }
        if (str[1] && str[1].length >= 4) {
            str[1] = str[1].replace(/(\d{3})/g, '$1 ');
        }
        data = str.join('.');
        if(data.toString().indexOf('.') < 0) {
            data = data + '.00';
        }
      }
      break;
    case '#.###,##':
      let str = data.toString();
      if (str.length > 5) {
        str = str.slice(0, -5) + '.' + str.slice(-3) + '.' + str.slice(-3);
        data = str;
        if(data.indexOf(',') < 0) {
          data = data + ',00';
        }
      }
      break;
    default:
      break;
    }
  }
  return data;
}

var changeDataFormat = function (item, dateFormat, numberFormat) {
    if(!isNaN(item)) {
        // item = formatNumber(item, numberFormat);
        item = item;
    } else {
      item = formatDate(item, dateFormat);
    }
    return item;
};

export default changeFormat;

